package Com.nexttech.Java_groupstudy;

public class Java_gst1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//how to print a int
		int wholenumber=100;
		System.out.println(wholenumber);
		
		
		//how to print float number/decimal
		double decimal_number=100.5;
		System.out.println(decimal_number);
		
		//How to print single character
		
		char single_alphabet='A';
		System.out.println(single_alphabet);
		
		//How to print word/string value
		
		String word="Word";
		System.out.println(word);

	}

}
